import { ChangeDetectionStrategy, Component, OnInit } from '@angular/core';
import { liveQuery } from 'dexie';
import { CrudApiService } from 'src/services/crud-api.service';
import { db, TodoList } from 'src/services/db';

@Component({
  changeDetection: ChangeDetectionStrategy.OnPush,
  selector: 'page-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  todoLists$ = liveQuery(() => db.todoLists.toArray());
  listName = 'My new list';

  constructor(private crudApiService: CrudApiService) { }

  ngOnInit(): void {
    this.crudApiService.GetCrucrudInfos().pipe(
    ).subscribe((val: any) => {
      console.log("ğŸ’€ğŸ’€ >> ", val)
    })
  }

  async addNewList() {
    await db.todoLists.add({
      title: this.listName,
    });

    this.crudApiService.PostCrudcrudInfos(this.listName).pipe(
    ).subscribe((datas: any) => {
      console.log("datas >>> ", datas)
    })
  }

  async resetDatabase() {
    await db.resetDatabase();
  }

  identifyList(index: number, list: TodoList) {
    return `${list.id}${list.title}`;
  }
}
