import { ChangeDetectionStrategy, ChangeDetectorRef, Component, Input } from '@angular/core';
import { StringService } from 'cocori-ng/src/feature-core';
import { liveQuery } from 'dexie';
import { db, TodoItem, TodoList } from 'src/services/db';

import { CrudApiService } from '../../../../services/crud-api.service';

@Component({
  changeDetection: ChangeDetectionStrategy.OnPush,
  selector: 'item-list',
  templateUrl: './item-list.component.html',
  styleUrls: ['./item-list.component.scss'],
})
export class ItemListComponent {
  @Input() todoList!: TodoList;

  constructor(
    private cdr: ChangeDetectorRef,
    private crudApiService: CrudApiService,) { }

  // Observe an arbritary query:
  todoItems$ = liveQuery(() => this.listTodoItems());

  async listTodoItems() {
    return await db.todoItems
      .where({
        todoListId: this.todoList.id,
      })
      .toArray();
  }

  async addItem() {

    this.itemName = new StringService(this.itemName)
      .removeAllSpaces()
      .replaceAllAccentByNonAccentCharacters()
      .toString()

    await db.todoItems.add({
      title: this.itemName,
      todoListId: this.todoList.id,
    });

    this.crudApiService.NewListRessouce(this.todoList.title, this.itemName).pipe(
    ).subscribe(() => {
      this.getTodoItemsFromListNameAPI()
    })
  }

  private getTodoItemsFromListNameAPI() {
    this.crudApiService.GetCrucrudInfos(this.todoList.title).pipe(
    ).subscribe((todoItems: TodoItem[]) => {
      this.todoList.todoItems = todoItems

      this.cdr.detectChanges()
    })
  }

  trackBy(index: number) {
    return index;
  }

  itemName = 'My new item';
}
