import { ChangeDetectionStrategy, Component, OnInit } from '@angular/core';
import { liveQuery } from 'dexie';
import { from, map, mergeMap, toArray } from 'rxjs';
import { CrudApiService } from 'src/services/crud-api.service';
import { db, TodoList } from 'src/services/db';

@Component({
  changeDetection: ChangeDetectionStrategy.OnPush,
  selector: 'page-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  todoLists$ = liveQuery(() => db.todoLists.toArray());
  listName = 'My new list';
  lists: string[] = [];

  constructor(private crudApiService: CrudApiService) { }

  ngOnInit(): void {

    // from([1,2,3,4]).pipe(
    //   mergeMap(param => this.getListTodos(param))
    // ).subscribe(val => console.log(val));

    /** all the lists created */
    this.crudApiService.GetCrucrudInfos('').pipe(
      mergeMap((lists: string[]) =>
        // `from` emits each contact separately
        from(lists).pipe(
          // load each contact
          mergeMap((list: string) => this.getListTodos(list)),
          // collect all contacts into an array
          toArray(),
          // add the newly fetched data to original result
          map(list => ({ ...this.lists, list })),
        ))
    ).subscribe((datas) => {
      console.log("datas >>>>>>>>>>> ", datas)
    })
  }

  private getListTodos(list: string) {
    var obj: any = {};

    console.log("list >> ", list)
    // return of(`retrieved new data with param ${list}`).pipe(
    //   delay(1000)
    // )

    return this.crudApiService.GetCrucrudInfos(list).pipe(
      map(datas => {
        console.log("ğŸ’© ", list)
        return obj.list = datas
      })
        // return ({name: datas})})
    )
  }

  async addNewList() {
    await db.todoLists.add({
      title: this.listName,
    });

    this.crudApiService.NewRessouce(this.listName).pipe(
    ).subscribe((datas: any) => {
      console.log("datas >>> ", datas)
    })
  }

  async resetDatabase() {
    await db.resetDatabase();
  }

  identifyList(index: number, list: TodoList) {
    return `${list.id}${list.title}`;
  }
}
