import { Component, Input } from '@angular/core';
import { liveQuery } from 'dexie';
import { db, TodoList } from 'src/services/db';

import { CrudApiService } from '../../../../services/crud-api.service';

@Component({
  selector: 'item-list',
  templateUrl: './item-list.component.html',
  styleUrls: ['./item-list.component.scss'],
})
export class ItemListComponent {
  @Input() todoList!: TodoList;

  constructor(private crudApiService: CrudApiService,) {

  }

  // Observe an arbritary query:
  todoItems$ = liveQuery(() => this.listTodoItems());

  async listTodoItems() {
    return await db.todoItems
      .where({
        todoListId: this.todoList.id,
      })
      .toArray();
  }

  async addItem() {
    await db.todoItems.add({
      title: this.itemName,
      todoListId: this.todoList.id,
    });

    this.crudApiService.NewListRessouce(this.todoList.title, this.itemName).pipe(
    ).subscribe(() => {
      console.log("Ressource ajoutÃ©e ðŸ¥°")
    })
  }

  trackBy(index: number) {
    return index;
  }

  itemName = 'My new item';
}
