import { ChangeDetectionStrategy, ChangeDetectorRef, Component, OnInit } from '@angular/core';
import { liveQuery } from 'dexie';
import { from, map, mergeMap, toArray } from 'rxjs';
import { CrudApiService } from 'src/services/crud-api.service';
import { db, TodoList } from 'src/services/db';

@Component({
  changeDetection: ChangeDetectionStrategy.OnPush,
  selector: 'page-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  todoLists$ = liveQuery(() => db.todoLists.toArray());
  listName = 'My new list';
  todoLists: any[] = [];

  constructor(
    private crudApiService: CrudApiService,
    private cdr: ChangeDetectorRef,) { }

  ngOnInit(): void {

    /** all the lists created */
    this.crudApiService.GetCrucrudInfos('').pipe(
      mergeMap((lists: string[]) =>
        // `from` emits each contact separately
        from(lists).pipe(
          // load each contact
          mergeMap((list: string) => this.getListTodos(list)),
          // collect all contacts into an array
          toArray(),
          // add the newly fetched data to original result
          map(todoLists => ({ ...this.todoLists, todoLists })),
        ))
    ).subscribe((datas: { todoLists: TodoList[] }) => {
      this.todoLists = datas.todoLists

      console.log("datas >>>>>>>>>>> ", datas)
      console.log("lists >>>>>>>>>>> ", this.todoLists)

      this.cdr.detectChanges()
    })

    this.todoLists$.subscribe((datas) => {
      console.log("ðŸ’€", datas)
    })
  }

  private getListTodos(list: string) {
    return this.crudApiService.GetCrucrudInfos(list).pipe(
      map(todoItems => ({ title: list, todoItems: todoItems }))
    )
  }

  // private getListTodos(list: string) {
  //   var obj: any = {};

  //   return this.crudApiService.GetCrucrudInfos(list).pipe(
  //     map(datas => {
  //       obj[list] = datas

  //       console.log("ðŸ’© ", list, obj)
  //       return obj
  //     })
  //     // return ({name: datas})})
  //   )
  // }

  async addNewList() {

    let listName: string = this.listName.replace(/\s/g,'').normalize("NFD").replace(/\p{Diacritic}/gu, "")

    await db.todoLists.add({
      title: listName,
    });

    this.crudApiService.NewListRessouce(listName).pipe(
    ).subscribe((datas: any) => {
      console.log("datas >>> ", datas)
    })
  }

  async resetDatabase() {
    await db.resetDatabase();
  }

  identifyList(index: number, list: TodoList) {
    return `${list.id}${list.title}`;
  }
}
